navigateToPage(context, page) {
  WidgetsBinding.instance.addPostFrameCallback(
    (_) {
      Navigator.push(
        context,
        CupertinoPageRoute<bool>(
          fullscreenDialog: false,
          builder: (_) => page,
        ),
      );
    },
  );
}
